def check_lp(inToken, OutToken):
    contract = web3.eth.contract(address=pancakeswap_factory, abi=factory2_abi)
    pair_address = contract.functions.getPair(inToken, OutToken).call()
    print(pair_address)
    if pair_address != '0x0000000000000000000000000000000000000000':
        pair_contract = web3.eth.contract(address=pair_address, abi=json.loads(lpabi))
        reserves = pair_contract.functions.getReserves().call()
        pooled = reserves[1] / (10 ** 18)
        symbol = pair_contract.functions.symbol().call()
        print(f"{datetime.datetime.fromtimestamp(time.time())}. {symbol} Pooled: {pooled}")
        return pooled
    else:
        print("Pooled false")
        return check_lp(inToken, OutToken)


