def handle_event(event, inToken, OutToken):
    print(web3.toJSON(event))

    pair = web3.toJSON(event)
    token0 = str(web3.toJSON(event['args']['token1']))
    token1 = str(web3.toJSON(event['args']['token0']))

    print("Pair: " + token0 + " " + token1)

    if (token0.upper().strip('"') == OutToken.upper() and token1.upper().strip('"') == inToken.upper()):
        print("Pair Found")
        print("Selling")
    elif (token0.upper().strip('"') == inToken.upper() and token1.upper().strip('"') == OutToken.upper()):
        print("Pair Found")
        print("Buying")
    else:
        print("Pair Not Found")


def main(inToken, OutToken):
    event_filter = check_pair(inToken, OutToken)
    print(event_filter)
    # block_filter = web3.eth.filter('latest')
    # tx_filter = web3.eth.filter('pending')
    loop = asyncio.get_event_loop()
    try:
        loop.run_until_complete(
            asyncio.gather(
                log_loop(event_filter, 2, inToken, OutToken),
                # log_loop(block_filter, 2),
                # log_loop(tx_filter, 2)
            )
        )
    finally:
        loop.close()


